func healthScoreColor(score int) string {
	switch {
	case score >= 80:
		return "text-success"
	case score >= 60:
		return "text-warning"
	default:
		return "text-danger"
	}
}

func severityColor(severity string) string {
	switch severity {
	case "critical":
		return "text-red-500"
	case "major":
		return "text-orange-500"
	case "minor":
		return "text-yellow-500"
	case "warning":
		return "text-yellow-400"
	default:
		return "text-blue-400"
	}
}

func bgColorClass(color string) string {
	switch color {
	case "text-blue-500":
		return "bg-blue-500"
	case "text-green-500":
		return "bg-green-500"
	case "text-yellow-500":
		return "bg-yellow-500"
	case "text-red-500":
		return "bg-red-500"
	default:
		return "bg-gray-500"
	}
}

func toDataAttr(title string) string {
	switch title {
	case "Total Devices":
		return "total-devices"
	case "Online Devices":
		return "online-devices"
	case "Active Faults":
		return "active-faults"
	case "Critical Faults":
		return "critical-faults"
	default:
		return ""
	}
}

